.hed {
  font-size: 3rem;
  line-height: 3rem;
  margin: 20px 0 20px;
  font-weight: bold;
  width: 150%;
  max-width: 90vw;
}

.dek {
  margin: 0;
  display: block;
  font-size: 1.5rem;
  line-height: 2.2rem;
  color: black;
  margin-top: 1rem;
  max-width: 90vw;
}

.byline {
  font-size: .95rem;
  line-height: 1rem;
  color: black;
  margin-top: 1rem;
}

a, a:visited, a:hover {
  color: black;
  cursor: pointer;
  text-decoration: none;
  /*border-bottom: 1px solid #EAE7D6;*/
  box-shadow: inset 0 -4px 0 #EAE7D6;
  transition: box-shadow 0.25s ease-out;
}

a:hover {
  color: black;
  /*background: #EAE7D6;*/
  box-shadow: inset 0 -20px 0 #EAE7D6;
}

pre {
  margin-top: 25px;
  margin-bottom: 25px;
}

pre code {
  background: #F2F3F2;
  color: black;
  padding: 20px 15px;
  width: 100%;
  display: block;
  overflow-x: auto;
  font-size: 12px;
  text-align: initial;
  font-style: normal;
}
code {
  background: #F2F3F2;
  color: black;
  padding: 1px 5px;
}



span.action {
  border-color: #5601FF;
  border-width: 2px;
  border-style: none none solid none;
  color: #5601FF;
  /*font-size: 0.9em;*/
  padding: -4px 5px;
  margin: 0 5px;
  cursor: pointer;
}

.idyll-dynamic {
  text-decoration: underline;
  text-decoration-style: dotted;
}

.idyll-action {
  text-decoration: underline;
  cursor: pointer;
}

.idyll-document-error {
  color: red;
  font-family: monospace;
}



.idyll-step-graphic {
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  position: absolute;
  height: 100%;
  overflow: hidden;
  margin: 0 auto;
  text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
  background: black;
}

.idyll-scroll-graphic {

  text-align: center;
  width: 100%;
}

.idyll-step-graphic img {
  flex-shrink: 0;
  min-width: 100%;
  min-height: 100%
}

.idyll-step-content {
  left: 0;
  right: 0;
  bottom: 0;
  position: absolute;
  color: white;
  padding: 10px;
  background: rgba(0, 0, 0, 0.8);
}

.idyll-stepper-control {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 100%;
}

.idyll-stepper-control-button {
  background: rgba(0, 0, 0, 0.7);
  color: white;
  font-weight: bold;
  padding: 15px 10px;
  cursor: pointer;
}

.idyll-stepper-control-button-previous {
  position: absolute;
  left: 10px;
}

.idyll-stepper-control-button-next {
  position: absolute;
  right: 10px;
}

.idyll-stepper {
  margin: 60px 0;
}

.idyll-scroll {
  margin-top: 25vh;
}

.idyll-scroll-text {
  padding: 0 0 50vh 0;
}

.idyll-scroll-text .idyll-step {
  margin: 0 0 90vh 0;
  padding: 50px;
  background: white;
}


/* annotated-text container */
.annotated-text {
  position: relative;
  display: inline-block;
  cursor: help;
}

.annotated-text,
.annotated-text:visited {
  background: #efefef;
  padding: 0 5px;
  transition: background 0.25s ease-out;
}

.annotated-text:hover {
  background: #ccc;
}

/* annotated-text CSS */
.annotated-text .annotation-text {
  visibility: hidden;
  border: solid 0.5px #666;
  box-shadow: 0 0 5px #ccc;
  background: #fff;
  text-align: left;
  padding: 5px;
  /* border-radius: 4px; */
  position: absolute;
  z-index: 1;
  font-size: 0.9em;
  line-height: 1.2;
}

.annotated-text .annotation-text {
  width: 250px;
  bottom: 120%;
  left: 50%;
  margin-left: -125px; /* Use half of the width (120/2 = 60), to center the annotated-text */
  opacity: 0;
  font-weight: initial;
}

.annotated-text:hover .annotation-text {
  opacity: 1;
  visibility: visible;
  transition: opacity 0.25s ease-out;
}

@media all and (max-width: 800px) {
  .annotated-text .annotation-text {
    width: 50vh;
  }
}

@media all and (max-width: 600px) {
  .annotated-text .annotation-text {
    width: 50vw;
    position: fixed;
    left: 50%;
    bottom: 20%;
  }
}



